import React, { useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import AuthServices from '../services/authServices';
import './Login.css';

function Login({ setIsLoggedIn }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        errors
    }, []);


    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await AuthServices.login({ username, password });
            const { token, Role } = response.data;
            AuthServices.setAuthToken(token);
            AuthServices.setRole(Role);
            setIsLoggedIn(true);
            setErrors(false);
            navigate('/');

        } catch (error) {
            console.log("error en try catch ")
            setErrors(true);
            console.error(error);

        }

    };

    return (

        <div className="login-container">
            <div className="content">
                <h2 style={{ color: "white" }} >Inicio de Seccion</h2>
                <form onSubmit={handleLogin}>
                    <div class="mb-3" >
                        <div className="input-group">
                            <span className="input-group-text">
                                <i className="bi bi-person"></i>
                            </span>
                            <input
                                type="text"
                                className="form-control"
                                id="username"
                                aria-describedby="emailHelp"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                placeholder="Username"
                                style={{ width: "300px" }}
                            />
                        </div>



                    </div>
                    <div class="mb-3">
                        <div className="input-group">
                            <span className="input-group-text">
                                <i className="bi bi-lock"></i>
                            </span>

                            <input type="password"
                                class="form-control"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="Password"
                            />
                        </div>
                    </div>
                    {errors ? (
                        <><small className="invalid-feedback" id="helpId" >
                        <i className="bi bi-exclamation-circle"> user o passwor incorrecto 1</i>
                    </small>  </>
                    ):(
                        <small className="invalid-feedback" id="helpId" >
                        <i className="bi bi-exclamation-circle"> user o passwor incorrecto 2</i>
                    </small>
                            )};


                <button type="submit" class="btn btn-secondary">Iniciar sesi√≥n</button>
            </form>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Link className="btn btn-primary " style={{ marginTop: "5%" }} to={"/UserCreate"}>
                    <i className="bi bi-person-plus"> Registrarse</i>
                </Link>
            </div>
        </div>
        
        </div >
        
    );

}
export default Login;
