import React, { useState, useEffect } from "react";
import carServices from "../services/carServices";
import { Link, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

const CarList = () => {
    const [Car, setCar] = useState([]);
    let navigate = useNavigate;

    useEffect(() => {
        getList();
    }, []);

    const getList = () => {
        carServices.getAll()
            .then(response => {
                setCar(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });

    };

    const remove = (id_car) => {

        const swalWithBootstrapButtons = Swal.mixin({
            customClass: {
              confirmButton: 'btn btn-success',
              cancelButton: 'btn btn-danger'
            },
            buttonsStyling: false
          })

          swalWithBootstrapButtons.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'No, cancel!',
            reverseButtons: true
          }).then((result) => {
            if (result.isConfirmed) {
              swalWithBootstrapButtons.fire(
                'Deleted!',
                'Your file has been deleted.',
                'success'
              )
            } else if (
              /* Read more about handling dismissals below */
              result.dismiss === Swal.DismissReason.cancel
            ) {
              swalWithBootstrapButtons.fire(
                'Cancelled',
                'Your imaginary file is safe :)',
                'error'
              )
            }
          })
    };

    return (

        <div className="card text bg-light mb-3">
            <div className="card-header">
                <Link className="btn btn-secondary" to={"/CarCreate"}>
                <i className="bi bi-plus-circle"> Registrarse Vehiculo </i>
                    </Link>

            </div>

            <div className="card-body ">
                <div className="table-responsive">
                    <table className="table table-striped border = 1">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Placa</th>
                                <th scope="col">Descripci√≥n</th>
                                <th scope="col">Cilindraje</th>
                                <th scope="col">Capacidad</th>
                                <th scope="col">Modelo</th>
                                <th scope="col">Tipo</th>
                                <th scope="col">Link de Imagen</th>

                            </tr>

                        </thead>
                        <tbody>
                            {Car && Car.map(
                                (car) => (
                                    <tr key={car.id_car}>
                                        <th scope="row">{car.id_car}</th>
                                        <td>{car.licence_plate}</td>
                                        <td>{car.description}</td>
                                        <td>{car.cylinder_capacity}</td>
                                        <td>{car.capacity}</td>
                                        <td>{car.model_year}</td>
                                        <td>{car.typeCar.description}</td>
                                        <td>{car.image}</td>

                                        <td>
                                            <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                                                <Link className="btn btn-secondary" to={"/CarUpdate/" + car.id_car}>
                                                <i className="bi bi-gear"> Actualizar</i>
                                            </Link>
                                            <button className="btn btn-danger" onClick={() => remove(car.id_car)}>
                                            <i className="bi bi-trash3"> Eliminar</i>

                                            </button>
                                            </div>

                                            </td>
                                    </tr>

                                )
                            )}
                        </tbody>

                    </table>

                </div>
            </div>
        </div>






    );
};

export default CarList;
