import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import UserServices from "../services/usernameServices";
import Swal from "sweetalert2";
const UserUpdate = () => {
    const { id_username } = useParams();

    const initialUserState = {
        id_username: null,
        name: "",
        lastName: "",
        username: "",
        password: "",
        email: ""
    };
    const [User, setUser] = useState(initialUserState);

    const getUser = id_username => {
        UserServices.get(id_username)
            .then(response => {
                setUser(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    useEffect(() => {

        if (id_username)
            getUser(id_username);
    }, [id_username]);

    const handleInputChange = event => {
        const { name, value } = event.target;
        setUser({ ...User, [name]: value });
    };


    const updateUser = () => {

        UserServices.update(User.id_username, User)
            .then(response => {
                console.log(response.data);
                Swal.fire({
                    position: 'top-center',
                    icon: 'success',
                    title: 'Usuario Actualizado Correctamente',
                    showConfirmButton: false,
                    timer: 2200
                })

            })
            .catch(e => {
                console.log(e);
            });


    };



    return (
        <div className="card ">

            <div className="card-body ">

                <h5>Actualizar Usuario del Id : {User.id_username}</h5>

                <blockquote className="blockquote mb-0 ">

                    <form onSubmit={e => {
                        e.preventDefault()
                        updateUser()
                    }}
                        className="row g-3 needs-validation my-3  border = 1">

                        <div className="col-md-3 position-relative">
                            <label for="name" className="form-label ">Nombre</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text">
                                    <i className="bi bi-pencil-square"></i>
                                </span>
                                <input type="text" className="form-control" id="name"
                                    value={User.name}
                                    onChange={handleInputChange}
                                    name="name" required />


                            </div>
                        </div>

                        <div className="col-md-3 position-relative">
                            <label for="lastName" className="form-label ">Apellido</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text">
                                    <i className="bi bi-pencil-square"></i>
                                </span>
                                <input type="text" className="form-control" id="lastName"
                                    value={User.lastName}
                                    onChange={handleInputChange}
                                    name="lastName" required />

                            </div>
                        </div>

                        <div className="col-md-6 position-relative">
                            <label for="email" className="form-label ">Email</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text">
                                    <i className="bi bi-envelope-at"></i>
                                </span>
                                <input type="email" className=" form-control" id="email"
                                    value={User.email}
                                    onChange={handleInputChange}
                                    name="email" required />
                            </div>
                        </div>


                        <div className="col-md-3 position-relative">
                            <label for="username" className="form-label">Usuario</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text">
                                    <i className="bi bi-person"></i>
                                </span>
                                <input type="text" className="form-control" id="username"
                                    value={User.username}
                                    name="username" required />
                            </div>        
                                <p style={{color:"gree"}}>Â¡Este campo no se puede modificar por seguridad!</p>
                        </div>

                        <div className="col-md-3 position-relative">
                            <label for="password" className="form-label">Password</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text">
                                    <i className="bi bi-key"></i>
                                </span>
                                <input type="password" className="form-control" id="password"
                                    value={User.password}
                                    onChange={handleInputChange}
                                    name="password" required />

                            </div>
                        </div>


                        <div className="col-12">
                            <button className="btn btn-secondary my-3 mx-2" type="submit" >

                                <i className="bi bi-gear"> Actualizar</i>
                            </button>

                            <Link className="btn btn-danger" to={"/"}>
                                <i className="bi bi-x-circle"> Cancelar</i>
                            </Link>

                        </div>
                    </form>



                </blockquote>
            </div>


        </div>
    );
};

export default UserUpdate;
